package com.gdlgxy.internshipcommunity;import android.app.Application;import android.util.Log;import com.gdlgxy.internshipcommunity.network.ApiService;public class CommunityApplication extends Application {    private static final String TAG = "CommunityApplication";    private static Application sApplication;    public static Application getApplication() {        if (sApplication == null) {            try {                sApplication = (Application) Class.forName("android.app.ActivityThread")                        .getMethod("currentApplication")                        .invoke(null, (Object[]) null);            } catch (Exception e) {                e.printStackTrace();            }        }        return sApplication;    }    @Override    public void onCreate() {        super.onCreate();        Log.d(TAG, "onCreate");        ApiService.init("http://123.56.232.18:8080/serverdemo", null);    }}