package com.gdlgxy.internshipcommunity.base;import android.os.Bundle;import android.view.LayoutInflater;import androidx.viewbinding.ViewBinding;import io.reactivex.disposables.CompositeDisposable;import io.reactivex.disposables.Disposable;import me.imid.swipebacklayout.lib.app.SwipeBackActivity;import me.imid.swipebacklayout.lib.app.SwipeBackActivityBase;public abstract class BaseActivity<T extends ViewBinding>        extends SwipeBackActivity        implements IGetPageName,IViewBinding.inflate_Activity<T>{    protected T viewBinding = null;    private final CompositeDisposable compositeDisposable = new CompositeDisposable();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        viewBinding = inflate_Activity();        setContentView(viewBinding.getRoot());        setSwipeBackEnable(swipeBackEnable());    }    @Override    protected void onStart() {        super.onStart();    }    @Override    protected void onStop() {        super.onStop();    }    @Override    protected void onDestroy() {        compositeDisposable.dispose();        super.onDestroy();    }    protected boolean swipeBackEnable() {        return true;    }    protected void addDisposable(Disposable disposable){        compositeDisposable.add(disposable);    }}