package com.gdlgxy.internshipcommunity.base;import android.app.Activity;import android.content.Context;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.gdlgxy.internshipcommunity.module.mine.MineViewModel;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.fragment.app.Fragment;import androidx.lifecycle.ViewModel;import androidx.lifecycle.ViewModelProvider;import androidx.viewbinding.ViewBinding;import io.reactivex.disposables.CompositeDisposable;import io.reactivex.disposables.Disposable;public abstract class BaseFragment<T extends ViewBinding, V extends ViewModel> extends Fragment        implements IViewBinding.inflate_Fragment<T>, IGetPageName {    protected T mView = null;    protected V mViewModel = null;    private final CompositeDisposable compositeDisposable = new CompositeDisposable();    @Override    public void onAttach(@NonNull Context context) {        super.onAttach(context);    }    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        mView = this.inflate_Fragment(inflater, container, false);        mViewModel = createdViewModel();        return mView.getRoot();    }    @Override    public void onDestroyView() {        compositeDisposable.dispose();        super.onDestroyView();        mView = null;        mViewModel = null;    }    protected abstract V createdViewModel();    protected void addDisposable(Disposable disposable) {        compositeDisposable.add(disposable);    }}