package com.gdlgxy.internshipcommunity.base;import android.app.Activity;import android.content.Context;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.fragment.app.Fragment;import androidx.viewbinding.ViewBinding;import io.reactivex.disposables.CompositeDisposable;import io.reactivex.disposables.Disposable;public abstract class BaseFragment<T extends ViewBinding>        extends Fragment        implements IViewBinding.inflate_Fragment<T>,IGetPageName{    protected T viewBinding = null;    private Activity activity = null;    private final CompositeDisposable compositeDisposable = new CompositeDisposable();    @Override    public void onAttach(@NonNull Context context) {        super.onAttach(context);        activity = requireActivity();    }    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        viewBinding = this.inflate_Fragment(inflater,container,false);        return viewBinding.getRoot();    }    @Override    public void onDestroyView() {        compositeDisposable.dispose();        super.onDestroyView();        viewBinding = null;    }    protected void addDisposable(Disposable disposable){        compositeDisposable.add(disposable);    }}