package com.gdlgxy.internshipcommunity.base;import androidx.lifecycle.ViewModel;import io.reactivex.disposables.CompositeDisposable;import io.reactivex.disposables.Disposable;public abstract class BaseViewModel extends ViewModel implements IGetPageName {    private final CompositeDisposable compositeDisposable = new CompositeDisposable();    @Override    protected void onCleared() {        compositeDisposable.dispose();        super.onCleared();    }    protected void addDisposable(Disposable disposable) {        compositeDisposable.add(disposable);    }}