package com.gdlgxy.internshipcommunity.base;import android.view.LayoutInflater;import android.view.ViewGroup;import com.gdlgxy.internshipcommunity.constant.PageName;/** * ViewBinding 的具体实现 * 继承#BaseActivity&#BaseFragment需要实现此方法 * * @param <T> */public interface IViewBinding<T> {    interface inflate_Activity<T> {        T inflate_Activity(LayoutInflater layoutInflater);    }    interface inflate_Fragment<T> {        T inflate_Fragment(LayoutInflater layoutInflater, ViewGroup container, boolean at);    }}