package com.gdlgxy.internshipcommunity.module.homeimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.paging.PagingDataAdapterimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.gdlgxy.internshipcommunity.Rimport com.gdlgxy.internshipcommunity.widget.PPImageViewclass HomeAdapter : PagingDataAdapter<HomeData, HomeAdapter.ViewHolder>(COMPARATOR) {    companion object {        private val COMPARATOR = object : DiffUtil.ItemCallback<HomeData>() {            override fun areItemsTheSame(oldItem: HomeData, newItem: HomeData): Boolean {                return oldItem.id == newItem.id            }            override fun areContentsTheSame(oldItem: HomeData, newItem: HomeData): Boolean {                return oldItem == newItem            }        }    }    class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        val titleName: TextView = itemView.findViewById(R.id.home_user_title_text)        val description: TextView = itemView.findViewById(R.id.home_user_description_text)        val starCount: TextView = itemView.findViewById(R.id.home_user_start_count)        val shareImage: PPImageView = itemView.findViewById(R.id.home_user_share_image)        val userImage: PPImageView = itemView.findViewById(R.id.home_user_image)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {        val view =            LayoutInflater.from(parent.context).inflate(R.layout.fragment_home_item, parent, false)        return ViewHolder(view)    }    override fun onBindViewHolder(holder: ViewHolder, position: Int) {        val repo = getItem(position)        if (repo != null) {            holder.titleName.text = repo.name            holder.description.text = repo.description            holder.shareImage.setImageUrl(repo.owner.mage_url)            holder.userImage.setImageUrl(repo.owner.mage_url)            holder.starCount.text = repo.startCount.toString()        }    }}