package com.gdlgxy.internshipcommunity.module.home;import com.gdlgxy.internshipcommunity.AppConfig;import java.util.ArrayList;import java.util.List;import java.util.Objects;import androidx.lifecycle.LiveData;import androidx.lifecycle.MutableLiveData;import androidx.lifecycle.ViewModel;public class HomeFgViewModel extends ViewModel {    private MutableLiveData<HomeTabData> _mTabData = new MutableLiveData<>();    private final MutableLiveData<List<HomeTabData.Tabs>> _mTabItemData = new MutableLiveData<>();    private LiveData<HomeTabData> mTabData;    public LiveData<HomeTabData> requestTabData() {        _mTabData = new MutableLiveData<>();        _mTabItemData.setValue(new ArrayList<>());        _mTabData.setValue(AppConfig.getHomeTabConfig());        for (HomeTabData.Tabs tab : Objects.requireNonNull(_mTabData.getValue()).tabs) {            if (tab.enable) {                Objects.requireNonNull(_mTabItemData.getValue()).add(tab);            }        }        mTabData = _mTabData;        return mTabData;    }    public LiveData<List<HomeTabData.Tabs>> getTabItemLiveData(){        LiveData<List<HomeTabData.Tabs>> tabItemData = _mTabItemData;        return tabItemData;    }    public LiveData<HomeTabData> getTabLiveData(){        mTabData= _mTabData;        return mTabData;    }}