package com.gdlgxy.internshipcommunity.module.home;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.gdlgxy.internshipcommunity.base.BaseFragment;import com.gdlgxy.internshipcommunity.constant.PageName;import com.gdlgxy.internshipcommunity.databinding.FragmentHomeBinding;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.lifecycle.Observer;import androidx.lifecycle.ViewModelProvider;import androidx.viewbinding.ViewBinding;public class HomeFragment        extends BaseFragment<FragmentHomeBinding> {    private HomeViewModel homeViewModel;    private FragmentHomeBinding binding;    public View onCreateView(@NonNull LayoutInflater inflater,                             ViewGroup container, Bundle savedInstanceState) {        homeViewModel =                new ViewModelProvider(this).get(HomeViewModel.class);        binding = FragmentHomeBinding.inflate(inflater, container, false);        View root = binding.getRoot();        final TextView textView = binding.textHome;        homeViewModel.getText().observe(getViewLifecycleOwner(), new Observer<String>() {            @Override            public void onChanged(@Nullable String s) {                textView.setText(s);            }        });        return root;    }    @Override    public void onDestroyView() {        super.onDestroyView();        binding = null;    }    @Override    public FragmentHomeBinding inflate_Fragment(LayoutInflater layoutInflater, ViewGroup container, boolean at) {        return FragmentHomeBinding.inflate(layoutInflater,container,false);    }    @Override    public String getPageName() {        return PageName.HOME;    }}