package com.gdlgxy.internshipcommunity.module.home;import com.gdlgxy.internshipcommunity.base.BaseViewModel;import com.gdlgxy.internshipcommunity.constant.PageName;import androidx.lifecycle.LiveData;import androidx.lifecycle.MutableLiveData;public class HomeViewModel extends BaseViewModel {    private MutableLiveData<String> mText;    public HomeViewModel() {        mText = new MutableLiveData<>();        mText.setValue("This is home fragment");    }    public LiveData<String> getText() {        return mText;    }    @Override    public String getPageName() {        return PageName.HOME;    }}