package com.gdlgxy.internshipcommunity.module.homeimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport androidx.paging.PagingDataimport androidx.paging.cachedInimport kotlinx.coroutines.flow.Flowclass HomeViewModel : ViewModel() {    fun getPagingData(): Flow<PagingData<HomeData>> {        return Repository.getPagingData().cachedIn(viewModelScope)    }}