package com.gdlgxy.internshipcommunity.module.homeimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport androidx.paging.PagingDataimport androidx.paging.cachedInimport com.gdlgxy.internshipcommunity.repository.Repositoryimport kotlinx.coroutines.flow.Flowclass HomeViewModel : ViewModel() {    fun getPagingData(int: Int): Flow<PagingData<HomeData>> {        return Repository.getPagingData(int).cachedIn(viewModelScope)    }}