package com.gdlgxy.internshipcommunity.module.homeimport android.os.Bundleimport androidx.fragment.app.Fragmentimport androidx.fragment.app.FragmentManagerimport androidx.lifecycle.Lifecycleimport androidx.viewpager2.adapter.FragmentStateAdapterclass TopTabAdapter(    private var mData: List<HomeTabData.Tabs>,    fragmentManager: FragmentManager,    lifecycle: Lifecycle) :    FragmentStateAdapter(fragmentManager, lifecycle) {    override fun getItemCount(): Int {        return mData.size    }    override fun createFragment(position: Int): Fragment {        val page = HomeFragment()        page.setType(mData[position].tag)        return page    }}