package com.gdlgxy.internshipcommunity.module.internshipimport android.os.Bundleimport android.view.Viewimport android.widget.Toastimport androidx.fragment.app.viewModelsimport androidx.recyclerview.widget.GridLayoutManagerimport androidx.recyclerview.widget.RecyclerViewimport com.gdlgxy.internshipcommunity.module.internship.base.BaseFragmentimport com.gdlgxy.internshipcommunity.module.internship.base.list.XRecyclerViewimport com.gdlgxy.internshipcommunity.module.internship.base.list.base.BaseViewDataimport com.gdlgxy.internshipcommunity.module.internship.constant.PageNameimport com.gdlgxy.internshipcommunity.widget.GridItemDecorationimport com.gdlgxy.navannotationmodule.FragmentDestinationimport com.gdlgxy.internshipcommunity.databinding.FragmentInternshipBinding/** * 首页 */@FragmentDestination(pageUrl = "main/module/internship", asStarter = false)class InternshipFragment : BaseFragment<FragmentInternshipBinding>(FragmentInternshipBinding::inflate) {    private val viewModel: InternshipViewModel by viewModels()    companion object {        private const val HOME_SPAN_COUNT = 2    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        initView()    }    private fun initView() {        viewBinding.rvList.init(            XRecyclerView.Config()                .setViewModel(viewModel)                .setPullRefreshEnable(true)                .setPullUploadMoreEnable(true)                .setLayoutManager(GridLayoutManager(activity, HOME_SPAN_COUNT))                .setItemDecoration(GridItemDecoration(activity, HOME_SPAN_COUNT))                .setOnItemClickListener(object : XRecyclerView.OnItemClickListener {                    override fun onItemClick(parent: RecyclerView, view: View, viewData: BaseViewData<*>, position: Int, id: Long) {                        Toast.makeText(context, "条目点击: ${viewData.value}", Toast.LENGTH_SHORT).show()                    }                })                .setOnItemChildViewClickListener(object : XRecyclerView.OnItemChildViewClickListener {                    override fun onItemChildViewClick(parent: RecyclerView, view: View, viewData: BaseViewData<*>, position: Int, id: Long, extra: Any?) {                        if (extra is String) {                            Toast.makeText(context, "条目子View点击: $extra", Toast.LENGTH_SHORT).show()                        }                    }                }))    }    @PageName    override fun getPageName() = PageName.HOME    override fun onHiddenChanged(hidden: Boolean) {        super.onHiddenChanged(hidden)        // 这里可以添加页面打点    }}