package com.gdlgxy.internshipcommunity.module.internship;import com.gdlgxy.internshipcommunity.base.BaseViewModel;import com.gdlgxy.internshipcommunity.constant.PageName;import androidx.lifecycle.LiveData;import androidx.lifecycle.MutableLiveData;public class InternshipViewModel extends BaseViewModel {    private MutableLiveData<String> mText;    public InternshipViewModel() {        mText = new MutableLiveData<>();        mText.setValue("This is InternshipFragment");    }    public LiveData<String> getText() {        return mText;    }    @Override    public String getPageName() {        return PageName.INTERNSHIP;    }}