package com.gdlgxy.internshipcommunity.module.login;import com.gdlgxy.internshipcommunity.base.RBaseViewModel;import com.gdlgxy.internshipcommunity.data.User;import androidx.lifecycle.LiveData;import androidx.lifecycle.MutableLiveData;public class LoginViewModel extends RBaseViewModel {    private MutableLiveData<User> _mUser;    public LoginViewModel(){        super();        requestUser();    }    private void requestUser() {    }    public LiveData<User> getUser(){        _mUser.setValue(UserManager.get().getUser());        return new MutableLiveData<>(_mUser.getValue());    }}