package com.gdlgxy.internshipcommunity.module.mainpageconfig;import android.content.ComponentName;import com.gdlgxy.internshipcommunity.CommunityApplication;import com.gdlgxy.internshipcommunity.module.mainpageconfig.data.Destination;import java.util.HashMap;import androidx.fragment.app.FragmentActivity;import androidx.fragment.app.FragmentManager;import androidx.navigation.ActivityNavigator;import androidx.navigation.NavController;import androidx.navigation.NavGraph;import androidx.navigation.NavGraphNavigator;import androidx.navigation.NavigatorProvider;import androidx.navigation.fragment.FragmentNavigator;public class NavGraphBuilder {    public static void build(FragmentActivity activity, NavController controller, FragmentManager childFragmentManager, int container) {        NavigatorProvider provider = controller.getNavigatorProvider();        FixFragmentNavigator fragmentNavigator =                new FixFragmentNavigator(activity,                        childFragmentManager, container);        ActivityNavigator activityNavigator = provider.getNavigator(ActivityNavigator.class);        provider.addNavigator(fragmentNavigator);        NavGraph navGraph = new NavGraph(new NavGraphNavigator(provider));        HashMap<String, Destination> desConfig = DestinationConfig.getsDestConfig();        for (Destination value : desConfig.values()) {            if (value.isFragment()) {                FragmentNavigator.Destination destination = fragmentNavigator.createDestination();                destination.setClassName(value.getClassName());                destination.setId(value.getId());                destination.addDeepLink(value.getPageUrl());                navGraph.addDestination(destination);            } else {                ActivityNavigator.Destination destination = activityNavigator.createDestination();                destination.setComponentName(new ComponentName(CommunityApplication.getApplication().getPackageName()                        , value.getClassName()));                destination.setId(value.getId());                destination.addDeepLink(value.getPageUrl());                navGraph.addDestination(destination);            }            if (value.isAsStarter()) {                navGraph.setStartDestination(value.getId());            }        }        controller.setGraph(navGraph);    }}