package com.gdlgxy.internshipcommunity.module.mine;import android.app.Activity;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.gdlgxy.internshipcommunity.base.BaseFragment;import com.gdlgxy.internshipcommunity.databinding.FragmentMineBinding;import com.gdlgxy.internshipcommunity.login.LoginActivity;import com.gdlgxy.navannotationmodule.FragmentDestination;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.lifecycle.ViewModelProvider;@FragmentDestination(pageUrl = "main/module/mine", asStarter = false)public class MineFragment extends BaseFragment<FragmentMineBinding,MineViewModel> {    @Override    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {        super.onViewCreated(view, savedInstanceState);        Log.d("fragment-onViewCreated", "MineFragment");        mView.itemUser.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Context context = getContext();                if(context == null) {                    return;                }                Intent intent = new Intent(context, LoginActivity.class);                intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);                context.startActivity(intent);            }        });    }    @Override    public void onDestroyView() {        super.onDestroyView();    }    @Override    protected MineViewModel createdViewModel() {        return new ViewModelProvider(this).get(MineViewModel.class);    }    @Override    public String getPageName() {        return getClass().getName();    }    @Override    public FragmentMineBinding inflate_Fragment(LayoutInflater layoutInflater, ViewGroup container, boolean at) {        return FragmentMineBinding.inflate(layoutInflater, container, at);    }}