package com.gdlgxy.internshipcommunity.module.mine;import com.gdlgxy.internshipcommunity.base.BaseViewModel;import com.gdlgxy.internshipcommunity.constant.PageName;import com.gdlgxy.internshipcommunity.module.basepaging.PagingTabData;import androidx.lifecycle.LiveData;import androidx.lifecycle.MutableLiveData;import androidx.paging.DataSource;public class MineViewModel extends BaseViewModel<PagingTabData> {    private MutableLiveData<String> mText;    public MineViewModel() {        mText = new MutableLiveData<>();        mText.setValue("This is MineFragment");    }    @Override    public DataSource createDataSource() {        return null;    }    public LiveData<String> getText() {        return mText;    }    @Override    public String getPageName() {        return PageName.INTERNSHIP;    }}