package com.gdlgxy.internshipcommunity.module.mine;import com.gdlgxy.internshipcommunity.base.BaseViewModel;import com.gdlgxy.internshipcommunity.constant.PageName;import androidx.lifecycle.LiveData;import androidx.lifecycle.MutableLiveData;public class MineViewModel extends BaseViewModel {    private MutableLiveData<String> mText;    public MineViewModel() {        mText = new MutableLiveData<>();        mText.setValue("This is MineFragment");    }    public LiveData<String> getText() {        return mText;    }    @Override    public String getPageName() {        return PageName.INTERNSHIP;    }}