package com.gdlgxy.internshipcommunity.repositoryimport androidx.paging.Pagerimport androidx.paging.PagingConfigimport androidx.paging.PagingDataimport com.gdlgxy.internshipcommunity.module.home.HomeDataimport com.gdlgxy.internshipcommunity.module.home.HomePagingSourceimport com.gdlgxy.internshipcommunity.module.home.Serviceimport kotlinx.coroutines.flow.Flowobject Repository {    private const val PAGE_SIZE = 50    private val mService = Service.create()    fun getPagingData(int: Int): Flow<PagingData<HomeData>> {        return Pager(config = PagingConfig(PAGE_SIZE),            pagingSourceFactory = { HomePagingSource(mService, int) }).flow    }}