package com.gdlgxy.internshipcommunity.repository.student;import androidx.room.ColumnInfo;import androidx.room.Entity;import androidx.room.PrimaryKey;@Entitypublic class Student {    @PrimaryKey(autoGenerate = true)    public int id;    @ColumnInfo(name = "name", typeAffinity = ColumnInfo.TEXT)    public String name;    @ColumnInfo(name = "grade", typeAffinity = ColumnInfo.INTEGER)    public int grade;    public Student() {        this.name = "";        this.grade = 1;    }}