package com.gdlgxy.internshipcommunity.repository.student;import java.util.List;import androidx.room.Dao;import androidx.room.Delete;import androidx.room.Insert;import androidx.room.Query;import androidx.room.Update;@Daopublic interface StudentDao {    @Insert    void insertStudent(Student student);    @Update    void update(Student student);    @Delete    void delete(Student student);    @Query("SELECT * FROM student WHERE id =:id")    Student queryStudent(long id);    @Query("SELECT * FROM student")    List<Student> queryStudentList();}