package com.gdlgxy.internshipcommunity.repository.student;import android.content.Context;import androidx.annotation.NonNull;import androidx.room.Database;import androidx.room.Room;import androidx.room.RoomDatabase;import androidx.room.migration.Migration;import androidx.sqlite.db.SupportSQLiteDatabase;@Database(entities = {Student.class}, version = 1)public abstract class StudentDatabase extends RoomDatabase {    private static final String NAME = "student_db";    private static StudentDatabase sStudentDatabase;    public static synchronized StudentDatabase getInstance(Context context) {        if (sStudentDatabase == null) {            sStudentDatabase = Room.databaseBuilder(context.getApplicationContext(),                    StudentDatabase.class, NAME).build();        }        return sStudentDatabase;    }    public abstract StudentDao studentDao();    static final Migration MIGRATION_1_2 = new Migration(1, 2) {        @Override        public void migrate(@NonNull SupportSQLiteDatabase database) {            database.execSQL("CREATE TABLE temp_student ("                    + "id INTEGER PRIMARY KEY NOT NULL,"                    + "name TEXT,"                    + "grade INTEGER)");            database.execSQL("INSERT INTO temp_student (id,name,age)" +                    " SELECT id,name,grade FROM Student");            database.execSQL("ALTER TABLE temp_student RENAME TO student");        }    };}