package com.gdlgxy.internshipcommunity.ui;import android.content.ComponentName;import com.gdlgxy.internshipcommunity.AppGlobals;import com.gdlgxy.internshipcommunity.ui.data.Destination;import java.util.HashMap;import androidx.navigation.ActivityNavigator;import androidx.navigation.NavController;import androidx.navigation.NavGraph;import androidx.navigation.NavGraphNavigator;import androidx.navigation.NavigatorProvider;import androidx.navigation.fragment.FragmentNavigator;public class NavGraphBuilder {    public static void build(NavController controller){        NavigatorProvider provider = controller.getNavigatorProvider();        FragmentNavigator fragmentNavigator = provider.getNavigator(FragmentNavigator.class);        ActivityNavigator activityNavigator = provider.getNavigator(ActivityNavigator.class);        NavGraph navGraph = new NavGraph(new NavGraphNavigator(provider));        HashMap<String, Destination> desConfig = DestinationConfig.getsDestConfig();        for (Destination value : desConfig.values()){            if(value.isFragment()){                FragmentNavigator.Destination destination = fragmentNavigator.createDestination();                destination.setClassName(value.getClassName());                destination.setId(value.getId());                destination.addDeepLink(value.getPageUrl());                navGraph.addDestination(destination);            }else {                ActivityNavigator.Destination destination = activityNavigator.createDestination();                destination.setComponentName(new ComponentName(AppGlobals.getApplication().getPackageName()                ,value.getClassName()));                destination.setId(value.getId());                destination.addDeepLink(value.getPageUrl());                navGraph.addDestination(destination);            }            if(value.isAsStarter()){                navGraph.setStartDestination(value.getId());            }        }        controller.setGraph(navGraph);    }}